import React, {useState} from 'react';
import './AboutMeBlurb.css'

const AboutMeBlurb = (props) => {

    const [expanded, setExpanded] = useState(false);

    return (
        <div className='blurb__wrapper'>
            <h1 className='blurb__title' onClick={() => setExpanded(!expanded)}> About Me </h1>
            {expanded ? <p className='blurb__content'>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus mollis lobortis lectus, sit amet tempus nunc maximus in. Pellentesque lobortis lorem quis porttitor maximus. Integer dictum ultrices lacus, quis ultricies turpis fringilla a. Vestibulum vehicula tincidunt faucibus. Proin tempor, odio in semper pulvinar, augue nisi malesuada tortor, in luctus lectus turpis a massa. Praesent fermentum pharetra lacus vel rhoncus. In nec odio ligula</p> : null} 

        </div>
    );
}

export default AboutMeBlurb;
