import React from 'react';
import ReactDelayRender from 'react-delay-render';
import './Blurb.css'

const Blurb = (props) => {

    let classes = props.expanded ? 'blurb__content expanded' : 'blurb__content';

    return (
        <div className='blurb__wrapper'>
            <h1 className='blurb__title' > {props.title} </h1>
            {props.expanded ? <p className={classes}>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus mollis lobortis lectus, sit amet tempus nunc maximus in. Pellentesque lobortis lorem quis porttitor maximus. Integer dictum ultrices lacus, quis ultricies turpis fringilla a. Vestibulum vehicula tincidunt faucibus. Proin tempor, odio in semper pulvinar, augue nisi malesuada tortor, in luctus lectus turpis a massa. Praesent fermentum pharetra lacus vel rhoncus. In nec odio ligula</p> : null}
        </div>
    );
}

export default ReactDelayRender({delay: 500})(Blurb);
// export default Blurb;

// blurb will take another actual component
