import React, {useState} from "react";
import ReactDelayRender from "react-delay-render";
import FadeIn from "react-fade-in";
import Collapsible from "react-collapsible";

import SkillItem from "./SkillItem/SkillItem";

import "./Skills.css";

const Skills = () => {

  const [tabOpen, setTabOpen] = useState(-1);
  const [mobile, setMobile] = useState(window.innerWidth <= 600);

  return (
    <FadeIn>
      <div className="skills__wrapper">
        <Collapsible

        onOpening={ () => {if (mobile) {setTabOpen(1)}} }
        // onClose={ () => {if (mobile) {setTabOpen(0)}} }
        open={ tabOpen === 1 }
        trigger="Technical Skills">
          <p className="skills__paragraph">
            Here is a list of things I know about and absolutely subjective
            scale of my familiarity with them:
            <div className="skills__list">
              <SkillItem skill="HTML5" level="4" />
              <SkillItem skill="CSS (LESS,SASS)" level="4" />
              <SkillItem skill="Javascript" level="4" />
              <SkillItem skill="Bootstrap" level="4" />
              <SkillItem skill="React.js" level="3" />
              <SkillItem skill="Redux" level="3" />
              <SkillItem skill="GIT" level="3" />
              <SkillItem skill="RESTful APIs" level="3" />
              <SkillItem skill="jQuery" level="2" />
              <SkillItem skill="Python" level="1" />
            </div>
            <span>...and numerous other smaller libraries!</span>
          </p>
        </Collapsible>
        <Collapsible

        onOpening={() => {if (mobile) {setTabOpen(2)}} }
        // onClose={ () => {if (mobile) {setTabOpen(0)}} }
        open={tabOpen===2}
        trigger="Other Skills">
          <p className="skills__paragraph">
            And here is a couple of other skills that I have levelled-up throughout my
            career:
            <div className="skills__list">
              <SkillItem skill="Customer Management and Support"  />
              <SkillItem skill="Project Coordination"  />
              <SkillItem skill="Software Localization"  />
              <SkillItem skill="Community Management"  />
              <SkillItem skill="Team Coordination"  />
            </div>
          </p>
        </Collapsible>
        <Collapsible

        onOpening={() => {if (mobile) {setTabOpen(3)}} }
        onClose={ () => {if (mobile) {setTabOpen(0)}} }
        open={tabOpen===3}
        trigger="Hobbies/Interests">
          <p className="skills__paragraph">
            My main hobbies are weightlifting, video and board games,
            cooking and comedy of different kinds. Outside of web development, my
            passions include nutrition science, fitness, evolutionary
            psychology and video games industry. I love problem-solving and puzzles
            of all sorts and I am an active member of British Mensa.
          </p>
        </Collapsible>
      </div>
    </FadeIn>
  );
};

export default ReactDelayRender({ delay: 401 })(Skills);
