import React, { useState } from "react";
import ReactDelayRender from "react-delay-render";
import FadeIn from "react-fade-in";
import Collapsible from "react-collapsible";
import ProjectItem from "./ProjectItem/ProjectItem";

import kolorkiImg from "../../../../../assets/projects/kolorki.PNG";
import budgetImg from "../../../../../assets/projects/BudgControl.PNG";
import tdeeCalcImg from "../../../../../assets/projects/tdeeRedo.PNG";
import forkifyImg from "../../../../../assets/projects/forkifySmall.PNG";
import burgerImg from "../../../../../assets/projects/burgerBuilder.png";

import "./Projects.css";

const Projects = () => {
  const [mobile] = useState(window.innerWidth <= 600);

  const [tabOpen, setTabOpen] = useState({
    proj1: false,
    proj2: false,
    proj3: false,
    proj4: false,
    proj5: false
  });

  const triggerClicked = tab => {
    if (mobile) {
      setTabOpen({ [tab]: !tabOpen[tab] });
    } else {
      setTabOpen({ ...tabOpen, [tab]: !tabOpen[tab] });
    }
  };

  return (
    <FadeIn>
      <div className="projects__wrapper">
        <div className="projects__list">
          <Collapsible
            handleTriggerClick={() => triggerClicked("proj1")}
            open={tabOpen["proj1"]}
            trigger="Color Game"
          >
            <ProjectItem triggerTitle="Color Game" hrefLink="" img={kolorkiImg}>
              Simple DOM manipulation game where the user is challenged to guess
              the color by the RGB values.
            </ProjectItem>
          </Collapsible>
          <Collapsible
            handleTriggerClick={() => triggerClicked("proj2")}
            open={tabOpen["proj2"]}
            trigger="Budget Control"
          >
            <ProjectItem hrefLink="" img={budgetImg}>
              Simple app allowing you to manage your budget and check the
              percentage of each expense.
            </ProjectItem>
          </Collapsible>
          <Collapsible
            handleTriggerClick={() => triggerClicked("proj3")}
            open={tabOpen["proj3"]}
            trigger="TDEE Calculator"
          >
            <ProjectItem hrefLink="" img={tdeeCalcImg}>
              TDEE (Total Daily Energy Expenditure) app calculating your daily
              caloric requirements and recommending changes for weight loss
              based on 2 weeks of data input. Uses localStorage for persisting
              data.
            </ProjectItem>
          </Collapsible>
          <Collapsible
            handleTriggerClick={() => triggerClicked("proj4")}
            open={tabOpen["proj4"]}
            trigger="Forkify"
          >
            <ProjectItem hrefLink="" img={forkifyImg}>
              App using the Food2Fork API that allows you to look for recipes,
              save them as favorites and prepare shopping lists. Coded as a
              project with Jonas Schmedtimann as a part of his Udemy Course.
            </ProjectItem>
          </Collapsible>
          <Collapsible
            handleTriggerClick={() => triggerClicked("proj5")}
            open={tabOpen["proj5"]}
            trigger="Burger Builder (Work in Progress)"
          >
            <ProjectItem
              hrefLink={"https://filipzakrocki.github.io/burgerBuilder/#/"}
              img={burgerImg}
            >
              Build your own burger with this simple, mobile-friendly React.js
              App - orders are stored on Firebase server.
            </ProjectItem>
          </Collapsible>
        </div>
      </div>
    </FadeIn>
  );
};

export default ReactDelayRender({ delay: 401 })(Projects);
