import React from "react";
import "./Blurb.css";

import FadeIn from "react-fade-in";

const Blurb = props => {
  return (
    <div className="blurb__wrapper">
      <h1 className="blurb__title"> {props.title} </h1>
      {props.expanded ? <FadeIn>props.children</FadeIn> : null}
    </div>
  );
};

export default Blurb;

// blurb will take another actual component
